mapper.select:
SELECT ugs.*, ohi.hospital_name, ghc.reagent_name goods_name, hcs.specs, ghc.approvals, ofi.factory_name, gpc.reagent_name pgoods_name, ghc.goods_nature FROM hdi_unicode_goods_ship_approval ugs LEFT JOIN hdi_org_hospital_info ohi ON ugs.torg_id = ohi.id and ohi.del_flag=0 LEFT JOIN hdi_goods_hospital_reagent ghc ON ugs.tgoods_id = ghc.id and ghc.del_flag=0 LEFT JOIN hdi_goods_hospital_reagent_specs hcs ON ugs.tspecs_id = hcs.id and ugs.del_flag=0 LEFT JOIN hdi_org_factory_info ofi ON ghc.factory_id = ofi.id and ofi.del_flag=0 LEFT JOIN hdi_goods_platform_reagent gpc on ugs.pgoods_id = gpc.id and gpc.del_flag=0
ugs.torg_type = 0 AND ugs.tgoods_type = 2
AND ${deptIds}
AND ugs.ship_flag = #{shipFlag}
AND ugs.check_status = #{checkStatus}
AND ohi.hospital_name like CONCAT('%',#{hospitalName}, '%')
AND ghc.reagent_name like CONCAT('%', #{goodsName}, '%')
AND ofi.factory_name like CONCAT('%', #{factoryName}, '%')
ORDER BY IFNULL(ugs.editdate, ugs.credate) DESC

SELECT ugs.*, osi.supplier_name, gsc.reagent_name goods_name, scs.specs, gsc.approvals, ofi.factory_name, gpc.reagent_name pgoods_name, pcs.specs pspecs, gsc.goods_nature FROM hdi_unicode_goods_ship_approval ugs LEFT JOIN hdi_org_supplier_info osi ON ugs.torg_id = osi.id and osi.del_flag=0 LEFT JOIN hdi_goods_supplier_reagent gsc ON ugs.tgoods_id = gsc.id and gsc.del_flag=0 LEFT JOIN hdi_goods_supplier_reagent_specs scs ON ugs.tspecs_id = scs.id LEFT JOIN hdi_org_factory_info ofi ON gsc.factory_id = ofi.id and ofi.del_flag=0 LEFT JOIN hdi_goods_platform_reagent gpc on ugs.pgoods_id = gpc.id and gpc.del_flag=0 LEFT JOIN hdi_goods_platform_reagent_specs pcs on pcs.id = ugs.pspecs_id
ugs.torg_type = 1 AND ugs.tgoods_type = 2 and ugs.del_flag=0
AND ${deptIds}
AND ugs.ship_flag = #{shipFlag}
AND ugs.check_status = #{checkStatus}
AND osi.supplier_name like CONCAT('%',#{supplierName}, '%')
AND gsc.reagent_name like CONCAT('%', #{goodsName}, '%')
AND ofi.factory_name like CONCAT('%', #{factoryName}, '%')
ORDER BY IFNULL(ugs.editdate, ugs.credate) DESC
